function toggleMenu(t){t.target.closest(".btn-menu")&&document.body.classList.toggle("open-menu")}function toggleOption(t){if(t.target.closest(".op-button")){t.target.closest("option").classList.toggle("expanded");let e=window.getComputedStyle(document.querySelector("sub"));e.getPropertyValue("flex-direction").match("column")&&document.querySelector(`option:not([data-type="${t.target.closest("option").getAttribute("data-type")}"])`).classList.remove("expanded")}}function updateOption(t){if(t.target.closest("li")){t.target.closest("option").querySelector(".value").textContent=t.target.closest("li").querySelector("span").textContent,t.target.closest("option").querySelector("input").value=t.target.closest("li").querySelector("span").textContent,localStorage.setItem(t.target.closest("option").getAttribute("data-type"),t.target.closest("li").querySelector("span").textContent);try{t.target.closest("ul").querySelector("li.active").classList.remove("active")}catch{}t.target.closest("li").classList.add("active"),setTimeout(()=>{t.target.closest("option").classList.remove("expanded")},50)}}function closeOption(t){if(!t.target.closest("option")){let t=document.querySelectorAll("option");for(let e=0;e<t.length;e++)t[e].classList.remove("expanded")}}document.addEventListener("click",t=>{toggleMenu(t),toggleOption(t),updateOption(t),closeOption(t)}),document.addEventListener("keydown",t=>{"Enter"!==t.key&&" "!==t.key||(toggleMenu(t),toggleOption(t),updateOption(t),closeOption(t))});