document.addEventListener("click",t=>{if(t.target.closest(".btn-menu")&&document.body.classList.toggle("open-menu"),t.target.closest(".op-button")){t.target.closest("option").classList.toggle("expanded");let e=window.getComputedStyle(document.querySelector("sub"));e.getPropertyValue("flex-direction").match("column")&&document.querySelector(`option:not([data-type="${t.target.closest("option").getAttribute("data-type")}"])`).classList.remove("expanded")}if(t.target.closest("li")){t.target.closest("option").querySelector(".value").textContent=t.target.closest("li").querySelector("span").textContent,localStorage.setItem(t.target.closest("option").getAttribute("data-type"),t.target.closest("li").querySelector("span").textContent);try{t.target.closest("ul").querySelector("li.active").classList.remove("active")}catch{}t.target.closest("li").classList.add("active"),setTimeout(()=>{t.target.closest("option").classList.remove("expanded")},50)}if(!t.target.closest("option")){let t=document.querySelectorAll("option");for(let e=0;e<t.length;e++)t[e].classList.remove("expanded")}});